// import { createReducer } from 'react-redux';
import { configureStore, createSlice } from '@reduxjs/toolkit'

const initialCart = { show: false };
const initialCartItems = [
    { title: 'Test Item', quantity: 3, total: 18, price: 6 },
    { title: 'Test Item2', quantity: 3, total: 30, price: 10 }
];

const cartSlice = createSlice({
    name: 'cart',
    initialState: initialCart,
    reducers: {
        toggleShow(state){
            state.show = !state.show;
        }
    }
});

const cartItemsSlice = createSlice({
    name: 'cartItems',
    initialState: initialCartItems,
    reducers: {
        increment(state, index){
            state[index.payload].quantity++;
        },
        decrement(state, index){
            if(state[index.payload].quantity <= 1){
                state.splice(state[index.payload], 1);
            }
            state[index.payload].quantity--;
        }
    }
});

export const store = configureStore(
    { reducer: { cart: cartSlice.reducer, cartItems: cartItemsSlice.reducer }}
);

export const cartActions = cartSlice.actions;
export const cartItemsActions = cartItemsSlice.actions;
