// import { createReducer } from 'react-redux';
import { configureStore, createSlice } from '@reduxjs/toolkit'

const initialCart = { show: false };
const initialCartItems = { cartItems: {
    'Test Item': { title: 'Test Item', quantity: 3, total: 18, price: 6 },
    'Test Item2': { title: 'Test Item2', quantity: 3, total: 30, price: 10 }
}};

const cartSlice = createSlice({
    name: 'cart',
    initialState: initialCart,
    reducers: {
        toggleShow(state){
            state.show = !state.show;
        }
    }
});

const cartItemsSlice = createSlice({
    name: 'cartItems',
    initialState: initialCartItems,
    reducers: {
        increment(state){
            console.log('cartItems Increment: ' + state.title);
            initialCartItems[state.title].quantity++;
        },
        decrement(state){
            initialCartItems[state.title].quantity--;
        }
    }
});

// const cartReducer = createReducer((state = cart, action) => {
//     if(action.type === 'INCREMENT_QUANTITY'){
//         cartItemsIncrement(action.title);
//     }
//     if(action.type === 'DECREMENT_QUANTITY'){

//     }
//     if(action.type === 'TOGGLE_CART'){

//     }
// });

export const store = configureStore(
    { reducer: { cart: cartSlice.reducer, cartItems: cartItemsSlice.reducer }}
);

export const cartActions = cartSlice.actions;
export const cartItemsActions = cartItemsSlice.actions;
