// import { createReducer } from 'react-redux';
import { configureStore, createSlice } from '@reduxjs/toolkit'

const cart = { show: false };
const cartItem = { cartItems: { 0: { title: 'Test Item', quantity: 3, total: 18, price: 6 } } };

const cartSlice = createSlice({
    name: 'cart',
    cart: { show: false },
    reducers: {
        toggleShow(){
            cart.show = !cart.show;
        }
    }
});

const cartItemSlice = createSlice({
    name: 'cartItem',
    cartItem,
    reducers: {
        increment(title){
            cart.cartItems[title].quantity++;
        },
        decrement(title){
            cart.cartItems[title].quantity--;
        }
    }
});

// const cartReducer = createReducer((state = cart, action) => {
//     if(action.type === 'INCREMENT_QUANTITY'){
//         cartItemIncrement(action.title);
//     }
//     if(action.type === 'DECREMENT_QUANTITY'){

//     }
//     if(action.type === 'TOGGLE_CART'){

//     }
// });

export const store = configureStore(
    { reducer: { cart: cartSlice.reducer, cartItem: cartItemSlice.reducer }}
);

export const cartActions = cartSlice.actions;
export const cartItemActions = cartItemSlice.actions;
