import { createSlice } from '@reduxjs/toolkit'

const initialCartItems = {
    'Test Item': { title: 'Test Item', quantity: 3, total: 18, price: 6 },
    'Test Item2': { title: 'Test Item2', quantity: 3, total: 30, price: 10 }
};

export const cartItemsSlice = createSlice({
    name: 'cartItems',
    initialState: initialCartItems,
    reducers: {
        increment(state, index){
            state[index.payload].quantity++;
        },
        decrement(state, index){
            if(state[index.payload].quantity <= 1){
                Object.keys(state).state.splice(state[index.payload], 1);
            } else {
                state[index.payload].quantity--;
            }
        },
        addToCart(state, product){
            console.log(product.payload);
            console.log(state);
            state[state.length] = product.payload;
            // console.log(state);

        }
    }
});
