import { useDispatch, useSelector } from 'react-redux';
import { cartActions } from '../../store/index.js';

import classes from './CartButton.module.css';

const CartButton = (props) => {
  const dispatch = useDispatch();
  const handleCart = () => {
    dispatch(cartActions.toggleShow());
  }
  const cartItems = useSelector(state => state.cartItems);
  var total = 0;
  const cartQty = Object.keys(cartItems).map(cartItemsKey => {
    total += parseInt(cartItems[cartItemsKey].quantity);
    return total;
  });

  return (
    <button className={classes.button} onClick={handleCart}>
      <span>My Cart</span>
      <span className={classes.badge}>{cartQty}</span>
    </button>
  );
};

export default CartButton;
