// import { createReducer } from 'react-redux';
import { configureStore, createSlice } from '@reduxjs/toolkit'

const cart = {
    show: false,
    cartItems: { 0: { title: 'Test Item', quantity: 3, total: 18, price: 6 } }
};

const cartSlice = createSlice({
    name: 'cart',
    cart,
    reducers: {
        cartItemIncrement(title){
            cart.cartItems[title].quantity++;
        },
        cartItemDecrement(title){
            cart.cartItems[title].quantity--;
        },
        cartToggleShow(){
            cart.show = !cart.show;
        }
    }
});

