import { createSlice } from '@reduxjs/toolkit'

const initialCartItems = {
    'Test Item': { title: 'Test Item', quantity: 3, total: 18, price: 6 },
    'Test Item2': { title: 'Test Item2', quantity: 3, total: 30, price: 10 }
};

export const cartItemsSlice = createSlice({
    name: 'cartItems',
    initialState: initialCartItems,
    reducers: {
        increment(state, title){
            state[title.payload].quantity++;
        },
        decrement(state, title){
            if(state[title.payload].quantity <= 1){

                const newCartItems = {};
                Object.entries(state).filter(([title, cartItem]) => cartItem.title !== title.payload)
                .reduce(([title, prevState]) => {
                    newCartItems[title] = prevState[title];
                    console.log(prevState[title]);
                    return newCartItems;
                }, {});
                // const newCart = Object.keys(state).filter(key =>
                //     key !== title.payload).reduce((newState, key) =>
                //     {
                //         newState[key] = state[key];
                //         return newState;
                //     }, {}
                // );
                // console.log(newCartItems);
                // Object.keys(state).state.splice(state[title.payload], 1);
            } else {
                state[title.payload].quantity--;
            }
        },
        addToCart(state, product){
            const cartKey = product.payload.title;
            if(state[cartKey] === undefined){
                const newCartItem = product.payload;
                newCartItem.quantity = 1;
                newCartItem.total = product.payload.price;
                state[cartKey] = newCartItem;
            } else {
                state[cartKey].quantity++;
                state[cartKey].total = state[cartKey].quantity * state[cartKey].price;
            }
        }
    }
});
